// src/components/CustomNode.js

import React from 'react';
import { Handle, Position } from 'react-flow-renderer';

const CustomNode = ({ data, openModal }) => {
  return (
    <div style={styles.container}>
      <div>{data.label}</div>
      {/* Button to add the next node */}
      <button style={styles.button} onClick={() => openModal(data.id)}>
        Add Next
      </button>
      {/* Handles for connections */}
      {data.type !== 'start' && (
        <Handle
          type="target"
          position={Position.Top}
          style={styles.handle}
        />
      )}
      {data.type !== 'end' && (
        <Handle
          type="source"
          position={Position.Bottom}
          style={styles.handle}
        />
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: '10px',
    background: '#fff',
    color: '#000',
    borderRadius: '4px',
    border: '1px solid #222',
    position: 'relative',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  button: {
    marginTop: '10px',
    padding: '5px 10px',
    cursor: 'pointer',
    border: 'none',
    borderRadius: '4px',
    backgroundColor: '#007bff',
    color: '#fff',
    fontSize: '12px',
  },
  handle: {
    background: '#555',
  },
};

export default CustomNode;
