// src/Flow.js

import React, { useState } from 'react';
import ReactFlow, { Background, Controls, Handle, Position } from 'react-flow-renderer';
import Sidebar from './components/Sidebar';

// Custom node components
const StartNode = ({ data }) => {
  return (
    <div className="node start">
      {data.label}
      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

const TaskNode = ({ data }) => {
  return (
    <div className="node task">
      {data.label}
      <Handle type="source" position={Position.Bottom} />
    </div>
  );
};

// ... You can add more node components like DecisionNode and EndNode

const Flow = () => {
  const [nodes, setNodes] = useState([
    {
      id: '1',
      type: 'start',
      data: { label: 'Start' },
      position: { x: 250, y: 5 },
    },
  ]);

  const [edges, setEdges] = useState([]);

  // Function to add a new node
  const addNode = (type, position) => {
    const id = `${nodes.length + 1}`; // Generate a unique ID
    const newNode = {
      id,
      type,
      data: { label: type },
      position,
    };
    setNodes((prevNodes) => [...prevNodes, newNode]);
  };

  // Function to add a new edge
  const addEdge = (source, target) => {
    const newEdge = { id: `${source}-${target}`, source, target };
    setEdges((prevEdges) => [...prevEdges, newEdge]);
  };

  // Function to handle connecting nodes
  const onConnect = (params) => {
    addEdge(params.source, params.target);
  };

  return (
    <div className="flow-container">
      <Sidebar addNode={addNode} /> {/* Pass addNode to Sidebar */}
      <ReactFlow
        elements={[...nodes, ...edges]}
        onConnect={onConnect}
        style={{ width: '100%', height: '100%' }}
      >
        <Background color="#aaa" gap={16} />
        <Controls />
      </ReactFlow>
    </div>
  );
};

export default Flow;
