// Flow.js
import React, { useState, useCallback } from 'react';
import ReactFlow, { addEdge, Background } from 'react-flow-renderer';
import CustomNode from './CustomNode';
import TaskPopup from './TaskPopup';

const initialNodes = [
  { id: '1', type: 'input', data: { label: 'Start' }, position: { x: 250, y: 0 } },
];

const Flow = () => {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState([]);
  const [showPopup, setShowPopup] = useState(false);
  const [currentNodeId, setCurrentNodeId] = useState(null);

  // Task list (example)
  const tasks = [
    { id: '1', name: 'Worker Task (Simple)', description: 'Runs a Worker task.', icon: 'ðŸ”§', type: 'All' },
    { id: '2', name: 'Event Task', description: 'Runs an event task.', icon: 'ðŸ“…', type: 'All' },
    { id: '3', name: 'HTTP Task', description: 'Call an HTTP endpoint.', icon: 'ðŸŒ', type: 'System' },
    // Add more tasks as needed
  ];

  const nodeTypes = {
    customNode: (nodeProps) => (
      <CustomNode {...nodeProps} openModal={() => openTaskPopup(nodeProps.id)} />
    ),
  };

  const openTaskPopup = (nodeId) => {
    setCurrentNodeId(nodeId);
    setShowPopup(true);
  };

  const closeTaskPopup = () => {
    setShowPopup(false);
  };

  const onTaskSelect = (task) => {
    const newNode = {
      id: `${nodes.length + 1}`,
      type: 'customNode',  // Make sure this is the correct node type
      position: { x: 250, y: nodes.length * 100 },
      data: { label: task.name }
    };

    setNodes((nds) => [...nds, newNode]);
    closeTaskPopup();
  };

  return (
    <div className="flow-container">
      <ReactFlow
        nodes={nodes}
        edges={edges}
        nodeTypes={nodeTypes}
        onNodesChange={setNodes}
        onEdgesChange={setEdges}
        onConnect={(params) => setEdges((eds) => addEdge(params, eds))}
      >
        <Background />
      </ReactFlow>

      {showPopup && (
        <TaskPopup tasks={tasks} onTaskSelect={onTaskSelect} closeModal={closeTaskPopup} />
      )}
    </div>
  );
};

export default Flow;
