// src/Flow.js

import React, { useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap } from 'react-flow-renderer';
import Sidebar from './components/Sidebar';

const nodeTypes = {
  task: ({ data }) => (
    <div className="node task" style={{ padding: '10px', background: '#fff', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  start: ({ data }) => (
    <div className="node start" style={{ padding: '10px', background: '#d1ffd1', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  end: ({ data }) => (
    <div className="node end" style={{ padding: '10px', background: '#ffdddd', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  decision: ({ data }) => (
    <div className="node decision" style={{ padding: '10px', background: '#fff0d1', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const Flow = () => {
  const [nodes, setNodes] = useState([
    {
      id: '1',
      type: 'start',
      data: { label: 'Start' },
      position: { x: 250, y: 5 },
    },
  ]);

  const [edges, setEdges] = useState([]);

  const addNode = (type, position) => {
    const id = `${nodes.length + 1}`;
    const newNode = {
        id,
        type,
        data: { label: type.charAt(0).toUpperCase() + type.slice(1) },
        position,
    };

    console.log("Current nodes before adding:", nodes); // Log current nodes
    console.log("Adding new node:", newNode); // Log the new node being added

    setNodes((prevNodes) => {
        const updatedNodes = [...prevNodes, newNode];
        console.log("Updated nodes after adding:", updatedNodes); // Log updated nodes
        return updatedNodes;
    });
};

// Inside the Flow component, after defining setNodes
useEffect(() => {
  console.log("Nodes state updated:", nodes);
}, [nodes]); // This will run whenever `nodes` changes

  const onConnect = (params) => {
    console.log("Connecting nodes:", params);
    const newEdge = { id: `${params.source}-${params.target}`, source: params.source, target: params.target };
    setEdges((prevEdges) => [...prevEdges, newEdge]);
  };

  // Debugging logs
  console.log("Current nodes:", nodes);
  console.log("Current edges:", edges);

  return (
    <div className="flow-container" style={{ height: '100vh', display: 'flex' }}>
      <Sidebar addNode={addNode} />
      <div style={{ flexGrow: 1 }}>
        <ReactFlow
          elements={[...nodes, ...edges]}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          style={{ width: '100%', height: '100%', border: '1px solid black' }} // Add border for debugging
        >
          <Background color="#aaa" gap={16} />
          <Controls />
          <MiniMap nodeColor="#0041d0" nodeStrokeColor="#fff" />
        </ReactFlow>
      </div>
    </div>
  );
};

export default Flow;
