// src/Flow.js

import React, { useState, useEffect } from 'react';
import ReactFlow, { Background, Controls, MiniMap } from 'react-flow-renderer';
import Sidebar from './components/Sidebar';

const nodeTypes = {
    task: ({ data }) => (
        <div style={{ padding: '10px', background: '#e0f7fa', border: '1px solid #222', borderRadius: '4px' }}>
            {data.label}
        </div>
    ),
};

const Flow = () => {
    const [nodes, setNodes] = useState([]);
    const [edges, setEdges] = useState([]);

    const addNode = (type, position) => {
        const id = `${nodes.length + 1}`;
        const newNode = {
            id,
            type,
            data: { label: type.charAt(0).toUpperCase() + type.slice(1) },
            position,
        };
        setNodes((prevNodes) => [...prevNodes, newNode]);
    };

    const onConnect = (params) => {
        const newEdge = {
            id: `${params.source}-${params.target}`,
            source: params.source,
            target: params.target,
        };
        console.log("Creating edge:", newEdge);
        setEdges((prevEdges) => [...prevEdges, newEdge]);
    };

    useEffect(() => {
        console.log("Nodes state updated:", nodes);
    }, [nodes]);

    useEffect(() => {
        console.log("Edges state updated:", edges);
    }, [edges]);

    return (
        <div className="flow-container" style={{ height: '100vh', display: 'flex' }}>
            <Sidebar addNode={addNode} />
            <div style={{ flexGrow: 1 }}>
                <ReactFlow
                    elements={[...nodes, ...edges]}
                    onConnect={onConnect}
                    nodeTypes={nodeTypes}
                    style={{ width: '100%', height: '100%', border: '1px solid black' }}
                >
                    <Background color="#aaa" gap={16} />
                    <Controls />
                    <MiniMap nodeColor="#0041d0" nodeStrokeColor="#fff" />
                </ReactFlow>
            </div>
        </div>
    );
};

export default Flow;
