// src/components/Sidebar.js

import React from 'react';

const Sidebar = ({ addNode }) => {
  return (
    <div className="sidebar">
      <h2>Workflow Editor</h2>
      <button onClick={() => addNode('task', { x: 100, y: 100 })}>Add Task</button>
      <button onClick={() => addNode('decision', { x: 200, y: 200 })}>Add Decision</button>
      <button onClick={() => addNode('end', { x: 300, y: 300 })}>Add End</button>
      {/* Ensure buttons are not rendered conditionally or inside loops */}
    </div>
  );
};

export default Sidebar;
