// src/Flow.js

import React from 'react';
import ReactFlow, { Background, Controls, MiniMap } from 'react-flow-renderer';
import Sidebar from './components/Sidebar';

const nodeTypes = {
    task: ({ data }) => (
        <div style={{ padding: '10px', background: '#e0f7fa', border: '1px solid #222', borderRadius: '4px' }}>
            {data.label}
        </div>
    ),
};

const Flow = () => {
    // Hardcoded nodes
    const nodes = [
        {
            id: '1',
            type: 'task',
            data: { label: 'Start' },
            position: { x: 250, y: 0 },
        },
        {
            id: '2',
            type: 'task',
            data: { label: 'Task 1' },
            position: { x: 250, y: 100 },
        },
        {
            id: '3',
            type: 'task',
            data: { label: 'Task 2' },
            position: { x: 250, y: 200 },
        },
        {
            id: '4',
            type: 'task',
            data: { label: 'End' },
            position: { x: 250, y: 300 },
        },
    ];

    // Hardcoded edges
    const edges = [
        {
            id: 'e1-2',
            source: '1',
            target: '2',
            animated: true,
        },
        {
            id: 'e2-3',
            source: '2',
            target: '3',
            animated: true,
        },
        {
            id: 'e3-4',
            source: '3',
            target: '4',
            animated: true,
        },
    ];

    return (
        <div className="flow-container" style={{ height: '100vh', display: 'flex' }}>
            <Sidebar addNode={() => {}} /> {/* Placeholder for Sidebar functionality */}
            <div style={{ flexGrow: 1 }}>
                <ReactFlow
                    elements={[...nodes, ...edges]}
                    nodeTypes={nodeTypes}
                    style={{ width: '100%', height: '100%', border: '1px solid black' }}
                >
                    <Background color="#aaa" gap={16} />
                    <Controls />
                    <MiniMap nodeColor="#0041d0" nodeStrokeColor="#fff" />
                </ReactFlow>
            </div>
        </div>
    );
};

export default Flow;
