// src/Flow.js

import React, { useState } from 'react';
import ReactFlow, { Background, Controls, MiniMap } from 'react-flow-renderer';
import Sidebar from './components/Sidebar';

// Define node types for rendering
const nodeTypes = {
  task: ({ data }) => (
    <div className="node task" style={{ padding: '10px', background: '#fff', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  start: ({ data }) => (
    <div className="node start" style={{ padding: '10px', background: '#d1ffd1', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  end: ({ data }) => (
    <div className="node end" style={{ padding: '10px', background: '#ffdddd', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
  decision: ({ data }) => (
    <div className="node decision" style={{ padding: '10px', background: '#fff0d1', border: '1px solid #222', borderRadius: '4px' }}>
      <strong>{data.label}</strong>
    </div>
  ),
};

const Flow = () => {
  const [nodes, setNodes] = useState([
    {
      id: '1',
      type: 'start',
      data: { label: 'Start' },
      position: { x: 250, y: 5 },
    },
  ]);

  const [edges, setEdges] = useState([]);

  // Function to add nodes
  const addNode = (type, position) => {
    const id = `${nodes.length + 1}`; // Unique ID generation
    const newNode = {
      id,
      type,
      data: { label: type.charAt(0).toUpperCase() + type.slice(1) }, // Capitalized label
      position,
    };

    console.log("Adding new node:", newNode); // Debug log
    setNodes((prevNodes) => [...prevNodes, newNode]); // Update nodes
  };

  // Connect nodes
  const onConnect = (params) => {
    console.log("Connecting nodes:", params); // Debug log
    const newEdge = { id: `${params.source}-${params.target}`, source: params.source, target: params.target };
    setEdges((prevEdges) => [...prevEdges, newEdge]); // Update edges
  };

  // Debugging logs
  console.log("Current nodes:", nodes);
  console.log("Current edges:", edges);

  return (
    <div className="flow-container" style={{ height: '100vh', display: 'flex' }}>
      <Sidebar addNode={addNode} />
      <div style={{ flexGrow: 1 }}>
        <ReactFlow
          elements={[...nodes, ...edges]} // Combine nodes and edges
          onConnect={onConnect}
          nodeTypes={nodeTypes} // Use custom node types
          style={{ width: '100%', height: '100%' }}
        >
          <Background color="#aaa" gap={16} />
          <Controls />

        </ReactFlow>
      </div>
    </div>
  );
};

export default Flow;
