// src/components/Sidebar.js

import React from 'react';
import { useFlowContext } from '../Flow'; // Assuming Flow.js exports a context

const Sidebar = () => {
  const { addNode } = useFlowContext();

  return (
    <div className="sidebar">
      <button onClick={() => addNode('task', { x: 100, y: 100 })}>Add Task</button>
      <button onClick={() => addNode('decision', { x: 200, y: 200 })}>Add Decision</button>
      {/* Add more buttons for different node types */}
    </div>
  );
};

export default Sidebar;
